import PySimpleGUI as sg

DOOR = b''

layout = [[sg.VPush()],
        [sg.Button (image_data=sg.DEFAULT_BASE64_ICON,
                    border_width=0,
                    key="-TOMBOL-1-"),
        sg.Button (image_data=DOOR,
                border_width=0,
                key="-KELUAR-")],
        [sg.VPush()]]

window = sg.Window(
    title="Penggunaan Gambar untuk Tombol",
    layout=layout,
    icon="favicon.ico",
    element_justification="center",
    size=(500, 500))

while True:
    kejadian, nilai = window.read()
    if kejadian in (sg.WINDOW_CLOSED, "-KELUAR-"):
        break
    if kejadian == "-TOMBOL-1-":
        pass

window.close()